import React from 'react'
import "./ItemQuestion.css"
import axios from 'axios'
function ItemQuestion() {
    const [data, setData] = React.useState([]);
    const [isLoading, setIsLoading] = React.useState(true);
    const [isError, setIsError] = React.useState(false);
    React.useEffect(() => {
        const fetchData = async () => {
            setIsError(false);
            setIsLoading(true);
            try {
                const result = await axios(
                    'http://localhost:5000/api/question/question',
                );
                setData(result.data);
            } catch (error) {
                setIsError(true);
            }
            setIsLoading(false);
        };
        fetchData();
    }, []);
    return (
        <div className='container-item-question'>
            <div className='container-item-question-title'>
                <h3>Title</h3>
            </div>

            <div className='container-item-question-content'>
                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quidem!
                    Quasi, quisquam.
                    Quasi, quisquam.
                    Quasi, quisquam.
                    Quasi, quisquam.
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quidem!
                    Quasi, quisquam.
                    Quasi, quisquam.
                    
                </p>
            </div>
            <hr></hr>
            <div className='info-question'>
                <div className='info-question-author'>
                    <p>Author</p>
                </div>
                <div className='info-question-date'>
                    <p>Date</p>
                </div>

            </div>
            
        </div>
    )
}

export default ItemQuestion
