import ScrollButton from "../../../layouts/frontend/ScrollButton";
import React, { useEffect, useState } from 'react';
import { Link, useHistory } from "react-router-dom";
import axios from "axios";
import swal from "sweetalert";

function ViewNewsItemDetail(props) {

    const history = useHistory();
    const [loading, setLoading] = useState(true);
    const [newsItem, setNewsItem] = useState([]);

    const newsItemCount = newsItem.length;

    useEffect(() => {

        let isMounted = true;
        const news_slug = props.match.params.newsitem;
        const newsitem_slug = props.match.params.slug;

        axios.get(`/api/fetchnewsitems/${newsitem_slug}`).then(res => {
            if (isMounted) {
                if (res.data.status === 200) {
                    setNewsItem(res.data.newsItem_data.newsItem);
                    setNews(res.data.newsItem_data.news);
                    setLoading(false);
                }
                else if (res.data.status === 200) {
                    swal("Warning", res.data.message, "");
                }
                else if (res.data.status === 404) {
                    history.push('/news');
                    swal("Warning", res.data.message, "error");
                }
            }
        });
        return () => {
            isMounted = false;
        };
    }, [props.match.params.slug, history]);

    return (
        <div>
            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Chi tiết Tin Tức</h1>
                        </div>
                    </div>
                </div>
            </header>
            <div className="py-3 bg-warning mb-3">
                <div className="container">
                    <h6 className="text-capitalize"><Link className="text-decoration-none" style={{ color: 'black' }} to="/news">Danh mục</Link>/News/NewsItem</h6>
                    {/* <h6 className="text-capitalize"><Link className="text-decoration-none" style={{ color: 'black' }} to="/news">Danh mục</Link> / {news.name}</h6> */}
                </div>
            </div>
            <h4>NewsItemDetails</h4>
            {/* {showNewsItemList} */}
            <ScrollButton />
        </div>
    );
}
export default ViewNewsItemDetail;
