import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../../layouts/frontend/Navbar";
import "./AskQuestion.css";
import Footer from "../../../layouts/frontend/Footer";
import { useHistory } from "react-router-dom";
import axios from "axios";
function ViewQuestion() {
  const [categoryQuestionList, setCategoryQuestionList] = useState([]);
  const [tagList, setTagList] = useState([]);
  const [questionInput, setQuestionInput] = useState({
    title:'',
    category_question_id:'',
    content:'',
    tag_id:'',
  });
  
  const [errorList, setErrorList] = useState([]);

  const handleInput = e => {
    e.persist();
    setQuestionInput({...questionInput, [e.target.name]: e.target.value});
  };

  // Get list category question
  useEffect(() => {
    axios.get(`api/get-all-category-question`).then(res => {
      setCategoryQuestionList(res.data.categoryQuestions);
    });
  }, []);

  // Get list tag
  useEffect(() => {
    axios.get(`api/get-all-tag`).then(res => {
      setTagList(res.data.tags);
    });
  }, []);
  
  const submitQuestion = e => {
    e.preventDefault();

    

  }

  return (
    <div>
      <Navbar />
      <header className="ex-header">
        <div className="container">
          <div className="row">
            <div className="col-xl-10 offset-xl-1">
              <h1 className="text-center">Đặt câu hỏi</h1>
            </div>
          </div>
        </div>
      </header>
      <div className="question-container">
        <div className="hint">
          <p>
            {" "}
            Hãy đặt câu hỏi phỏng vấn liên quan đến công việc của bạn sắp tới
          </p>
        </div>
        <div className="main-form">
          <form>
            <div className="form-group">
              <label htmlFor="title">
                Tiêu đề <b>*</b>
              </label>
              <input
                type="text"
                className="form-control"
                id="title"
                placeholder="Nhập tiêu đề câu hỏi"
              />
            </div>
            <div className="note">
              <p>
                Vui lòng đặt tiêu đề thích hợp cho câu hỏi để người khác được
                trả lời dễ dàng hơn.
              </p>
            </div>
            {/* Create category */}
            <div className="form-group">
              <label>
                Danh mục nghề nghiệp <b>*</b>
              </label>
              <select className="form-control" id="exampleFormControlSelect1">
                <option>Chọn danh mục</option>
                <option>Lập trình</option>
                <option>Kỹ thuật</option>
                <option>Kỹ thuật viên</option>
                <option>Kỹ thuật số</option>
                <option>Kỹ thuật máy tính</option>
              </select>
            </div>
            <p>Vui lòng chọn danh mục thích hợp</p>
            {/* End create category */}
            <div class="form-group">
              <label for="">Nội dung</label>
              <textarea
                class="form-control"
                name=""
                id=""
                rows="3"
                placeholder="Nội dung/Đoạn code"
              ></textarea>
            </div>
            <p>Bạn có thể không nhập nội dung</p>
            {/* Create a tag*/}
            <div className="form-group">
              <label>
                Thẻ <b>*</b>
              </label>
              <input
                type="text"
                className="form-control"
                id="title"
                placeholder="Nhập thẻ câu hỏi"
              />
            </div>
            <p>Vui lòng ghi tag phù hợp với câu hỏi</p>
            {/* End create a tag*/}
            {/* Create a ask question button*/}
            <div className="form-group">
              <button type="submit" className="btn btn-primary">
                Đặt câu hỏi
              </button>
            </div>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default ViewQuestion;
