import React, {useState, useEffect} from "react";
import Footer from "../../../layouts/frontend/Footer";
import "./styles.css";
import { useLocation } from "react-router-dom";
import axios from "axios";
const ChangePassword = () => {
    const username =
    useLocation().pathname.split("/")[
      useLocation().pathname.split("/").length - 2
    ];
    console.log(username);
    const [profileList, setProfileList] = useState([]);
    useEffect(() => {
        axios.get(`api/user/${username}`).then((res) => {
          if (res.data.status === 200) {
            setProfileList(res.data.data);
            console.log(res.data.data)
          }
        });
      }, []);

      var showProfile = "";
        showProfile = profileList.map((item) => {
            return (
              <>
                <div className="change-password-container">
                    <div className="old-password">
                        <h4>Mật khẩu cũ <b></b></h4>
                        <input type="password" placeholder="Nhập mật khẩu cũ" />

                      </div>
                  </div>
              </>
            );
        });
  return (
    <>
      <header className="ex-header">
        <div className="container">
          <div className="row">
            <div className="col-xl-10 offset-xl-1">
              <h1 className="text-center">Đổi mật khẩu</h1>
            </div>
          </div>
        </div>
      </header>
      
      {showProfile}
      <Footer/>
    </>
  );
};

export default ChangePassword;
