import ScrollButton from "../../../layouts/frontend/ScrollButton";
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import php from './php.png';

function ViewNews() {

    const [news, setNews] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        let is
        axios.get(`/api/getNews`).then(res => {
            if (res.data.status === 200) {
                // console.log(res.data.news);
                setNews(res.data.news);
                setLoading(false);
            }
        });
    });

    if (loading) {
        return <h4>Loading News...</h4>
    }
    else {
        var showNewsList = '';
        showNewsList = news.map((item) => {
            return (
                <div className="col-md-3" key={item.id}>
                    <div className="card">
                        <Link className="text-decoration-none" to={`viewnews/${item.slug}`}>
                            {/* <img src={php} style={{ width: '100%', height: '280px' }} alt={item.name} /> */}
                            <hr />
                            <div className="card-body">
                                <h5 className="card-title text-center">{item.name}</h5>
                                <p className="card-text">{item.description}</p>
                            </div>
                        </Link>
                    </div>
                </div>
            )
        });
    }

    return (
        <div>
            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Danh Mục Tin Tức</h1>
                        </div>
                    </div>
                </div>
            </header>

            <div className="container">
                <div className="row row-cols-1 row-cols-md-4 g-4 mt-5">
                    {showNewsList}
                </div>
            </div>
            <ScrollButton />
        </div>
    );
}

export default ViewNews;
