import React from 'react';
import { Link } from 'react-router-dom';


function AddNews() {
    return (
        <div className="container-fluid px-4">

        {/* {
            display_errors.map((item, key) => {
                return (
                    <p className="mb-1" key={key}>{item}</p>
                )
            })
        } */}
        <div className="card mt-4">
            <div className="card-header">
                <h4>Thêm danh mục
                    <Link to="/admin/view-news" className="btn btn-primary btn-sm float-end text-decoration-none">Xem danh mục</Link>
                </h4>
            </div>
            <div className="card-body">
                <form onSubmit={submitNews} id="NEWS-FORM">
                    <div className="form-group mb-2">
                        <label className="form-label">Tên danh mục</label>
                        <input type="text" name="name" onChange="handleInput" value={newsInput.value.name} className="form-control" />
                    </div>
                    <div className="form-group mb-2">
                        <label className="form-label">Slug</label>
                        <input type="text" name="slug" onChange="handleInput" value={newsInput.value.slug} className="form-control" />
                    </div>
                    <div className="form-group mb-2">
                        <label className="form-label">Mô tả</label>
                        <input type="text" name="description" onChange="handleInput" value={newsInput.value.description} className="form-control" />
                    </div>
                    <div className="form-group mb-2">
                        <label className="form-label">Trạng thái &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</label>
                        <input type="checkbox" name="status" onChange="handleInput" value={newsInput.value.name} className="form-check-input" />
                    </div>
                    <button type="submit" className="btn btn-primary px-4 mt-2">Thêm</button>
                </form>
            </div>
        </div>
    </div>
    );
}

export default AddNews;
