import React, {useState, useEffect} from "react";
import Footer from "../../../layouts/frontend/Footer";
import "./styles.css";
import { useLocation } from "react-router-dom";
import axios from "axios";
const ChangePassword = () => {
    const username =
    useLocation().pathname.split("/")[
      useLocation().pathname.split("/").length - 2
    ];
    console.log(username);
    const [profileList, setProfileList] = useState([]);
    useEffect(() => {
        axios.get(`api/user/${username}`).then((res) => {
          if (res.data.status === 200) {
            setProfileList(res.data.data);
            console.log(res.data.data)
          }
        });
      }, []);

      var showProfile = "";
        showProfile = profileList.map((item) => {
            return (
              <>
                <div className="change-password-container">
                    {/* <div className="old-password">
                        <h5>Mật khẩu cũ <b style={{color:"red", fontSize:"24px"}}>*</b></h5>
                        <input type="password" placeholder="Nhập mật khẩu cũ" />
                      </div>
                      <div className="new-password">
                        <h5>Mật khẩu mới <b style={{color:"red", fontSize:"24px"}}>*</b></h5>
                        <input type="password" placeholder="Nhập mật khẩu mới" />
                        </div>
                        <div className="confirm-password">
                        <h5>Xác nhận mật khẩu <b style={{color:"red", fontSize:"24px"}}>*</b></h5>
                        <input type="password" placeholder="Nhập lại mật khẩu mới" />
                        </div> */}

<div className="profile-container">
        <div className="left-container">
          <div className="avatar">
            <img
              src="https://gtjai.com.vn/wp-content/uploads/2021/07/avt.png"
              alt="avatar"
            />
            <div className="full-name">
              <h4>{item.fullname}</h4>
            </div>
            <i
              className="fas fa-edit"
              style={{ fontSize: "30px", marginTop: "20px" }}
            />
          </div>
        </div>
        <div className="right-container">
          <h4>Thông tin cá nhân</h4>
          <hr></hr>
          <div className="fullname">
            <h5>Họ và tên</h5>
            <h6>{item.fullname}</h6>
          </div>
          <div className="username">
            <h5>Tên đăng nhập</h5>
            <h6>{item.username}</h6>
          </div>
          <div className="email">
            <h5>Email</h5>
            <h6>{item.email}</h6>
          </div>
          <div className="phone">
            <h5>Số điện thoại</h5>
            <h6>{item.phone}</h6>
          </div>
          <div className="created_at">
            <h5>Ngày tạo tài khoản</h5>
            <h6>{item.created_at}</h6>
          </div>
        </div>
      </div>
                  </div>
              </>
            );
        });
  return (
    <>
      <header className="ex-header">
        <div className="container">
          <div className="row">
            <div className="col-xl-10 offset-xl-1">
              <h1 className="text-center">Đổi mật khẩu</h1>
            </div>
          </div>
        </div>
      </header>
      
      {showProfile}
      <Footer/>
    </>
  );
};

export default ChangePassword;
