import axios from "axios";
import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import swal from "sweetalert";

function Result(props) {

    const history = useHistory();
    const [answer, setAnswer] = useState([]);
    const [quiz, setQuiz] = useState([]);
    const [loading, setLoading] = useState(true);

    if (!localStorage.getItem('auth_token')) {
        history.push('/');
        swal("warning", "Login to view result", "error");
    }

    var item_id_quiz = quiz.map((item) => {
        return item.item_id;
    });

    useEffect(() => {

        let isMountered = true;

        axios.get(`/api/get-answer`).then(res => {
            if (isMountered) {
                if (res.data.status === 200) {
                    setAnswer(res.data.answer);
                    setLoading(false);
                } else if (res.data.status === 401) {
                    history('/');
                    swal("warning", res.data.message, "error");
                }
            }
        });

        axios.get(`/api/view-quiz`).then(res => {
            if (res.data.status === 200) {
                setQuiz(res.data.quizzes);
                setLoading(false);
            }
        });


        return () => {
            isMountered = false;
        }
    }, []);

    // console.log(answer);

    // const data = answer.map((item) => {
    //     return item.quiz_choice;
    // });

    console.log(quiz);

    // console.log(data);

    // const datanew = data.toString();

    // console.log(datanew);

    // const arr = JSON.parse(datanew);
    // console.log(arr);

    // const result = Object.values(arr);
    // console.log(result);

    if (loading) {
        return (
            <h4>Loading Result...</h4>
        )
    }

    return (
        <div>
            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Kết quả</h1>
                        </div>
                    </div>
                </div>
            </header>

            <div>
                ket qua
            </div>
        </div>
    )
}

export default Result;
