import React, { useState, useEffect } from "react";
import Footer from "../../../layouts/frontend/Footer";
import "./styles.css";
import { useLocation } from "react-router-dom";
import axios from "axios";
import swal from "sweetalert";
const ChangePassword = () => {
  const username =
    useLocation().pathname.split("/")[
      useLocation().pathname.split("/").length - 2
    ];
  console.log(username);
  const [profileList, setProfileList] = useState([]);
  useEffect(() => {
    axios.get(`api/user/${username}`).then((res) => {
      if (res.data.status === 200) {
        setProfileList(res.data.data);
        console.log(res.data.data);
      }
    });
  }, []);
  const [passwordList, setPasswordList] = useState({
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [errorList, setErrorList] = useState([]);
  const handleInput = (e) => {
    e.persist();
    setPasswordList({ ...passwordList, [e.target.name]: e.target.value });
  };
  const submitPassword = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("oldPassword", passwordList.oldPassword);
    formData.append("newPassword", passwordList.newPassword);
    formData.append("confirmPassword", passwordList.confirmPassword);
    axios.post(`api/user/${username}/change-password`, formData).then((res) => {
      if (res.data.status === 200) {
        swal("Thay đổi mật khẩu thành công", "", "success");
      } else {
        setErrorList(res.data.data);
      }
    });
  };
  var showProfile = "";
  showProfile = profileList.map((item) => {
    return (
      <>
        <div className="profile-container">
          <div className="left-container">
            <div className="avatar">
              <img
                src="https://gtjai.com.vn/wp-content/uploads/2021/07/avt.png"
                alt="avatar"
              />
              <div className="full-name">
                <h4>{item.fullname}</h4>
              </div>
              <i
                className="fas fa-edit"
                style={{ fontSize: "30px", marginTop: "20px" }}
              />
            </div>
          </div>
          <div className="right-container">
            <h4>Đổi mật khẩu</h4>
            <hr></hr>
            <form>

            </form>
            
          </div>
        </div>
      </>
    );
  });
  return (
    <>
      <header className="ex-header">
        <div className="container">
          <div className="row">
            <div className="col-xl-10 offset-xl-1">
              <h1 className="text-center">Đổi mật khẩu</h1>
            </div>
          </div>
        </div>
      </header>

      {showProfile}
      <Footer />
    </>
  );
};

export default ChangePassword;
