import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";
import swal from "sweetalert";

function ViewNewsItem() {

    const [loading, setLoading] = useState(true);
    const [viewNewsItem, setNewsItem] = useState([]);

    useEffect(() => {
        // document.title = "View News Items";
        axios.get(`/api/view-news-item`).then(res => {
            if (res.data.status === 200) {
                // console.log(res.data.newsitem);
                setNewsItem(res.data.newsitem);
                setLoading(false);
            };
        });
    }, []);

    if(loading){
        return <h4>View News Item Loading...</h4>
    }
    else{
        viewNewsItem.map((item) => {
            return (
                <tr>
                    <td>{item.id}</td>
                    <td>{item.news_id}</td>
                    <td>{item.name}</td>
                    <td>{item.slug}</td>
                    <td>{item.Description}</td>
                    <td>{item.Image}</td>
                    <td>{item.Edit}</td>
                    <td>{item.Delete}</td>
                    <td>{item.Status}</td>
                </tr>
            )
        });
    }

    return (
        <div className="container-fluid px-4">
            <div className="card mt-4">
                <div className="card-header">
                    <h4>View Item
                        <Link to="/admin/add-item-quiz" className="btn btn-primary btn-sm float-end text-decoration-none">Add Item</Link>
                    </h4>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table table-bordered table-striped">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>News Name</th>
                                    <th>Item Name</th>
                                    <th>Slug</th>
                                    <th>Description</th>
                                    <th>Time</th>
                                    <th>Image</th>
                                    <th>Edit</th>
                                    <th>Delete</th>
                                    <th>Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {/* {display_ItemData} */}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ViewNewsItem;
