import React, { useState, useRef } from "react";
import "../Question/ContentQuestion.css";
function AddComment() {
  return (
    <div className="comment-container">
      <div className="leave-comment">
        <h6>Leave a comment</h6>
      </div>
      <form  encType="multipart/form-data">
            
            <div class="form-group">
              <label for="">Nội dung</label>
              <textarea
                class="form-control"
                name="content"
                // onChange={handleInput}
                // value={questionInput.content}
                rows="3"
                placeholder="Nội dung/Đoạn code"
              ></textarea>
              
            </div>
            
                <button
                  className="add-tag-btn form-control-submit-button"
                  onClick={AddTag}
                >
                  <i className="fas fa-plus"></i>
                </button>
            <p>Vui lòng chọn tag phù hợp với câu hỏi</p>
            {/* End create a tag*/}
            {/* Create a ask question button*/}
            <div className="form-group">
              <button type="submit" className="form-control-submit-button">
                Đặt câu hỏi
              </button>
            </div>
          </form>
    
    </div>
  );
}

export default AddComment;
