import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import swal from "sweetalert";

const CategoryManagement = () => {
  const [loading, setLoading] = useState(true);
  const [CommentList, setCategoryList] = useState([]);
  useEffect(() => {
    axios.get(`api/category-question`).then((res) => {
      if (res.status === 200) {
        setCategoryList(res.data.data);
      }
      setLoading(false);
    });
  }, []);
  const deleteCategory = (e, id) => {
    e.preventDefault();
    const thisClicked = e.currentTarget;
    thisClicked.innerText = "Đang xóa";

    axios.delete(`api/category-question/delete/${id}`).then((res) => {
      if (res.data.status === 200) {
        swal("Success", res.data.message, "success");
        thisClicked.closest("tr").remove();
      } else if (res.data.status === 404) {
        swal("Success", res.data.message, "success");
        thisClicked.innerText = "Xóa";
      }
    });
  };
  var Comment_HTMLTABLE = "";
  if (loading) {
    return <h4>Đang tải trang danh mục...</h4>;
  } else {
    Comment_HTMLTABLE = CommentList.map((item, index) => {
      return (
        <tr key={item.id}>
          <td>{index + 1}</td>
          <td>{item.name}</td>
          <td>{item.slug}</td>
          <td>
            <Link
              to={`profile/edit/${item.id}`}
              className="btn btn-success btn-sm text-decoration mr-10"
            >
              Sửa
            </Link>
            <button
              type="button"
              onClick={(e) => deleteCategory(e, item.id)}
              className="btn btn-danger btn-sm"
            >
              Xóa
            </button>
          </td>
        </tr>
      );
    });
  }
  return (
    <div className="container px-4">
      <div className="card mt-4">
        <div className="card-header">
          <h4>
            Danh sách danh mục câu hỏi
            {/* <Link
              to="/admin/category-question/add"
              className="btn btn-warning btn-sm float-end text-decoration-none"
            >
              Thêm danh mục
            </Link> */}
          </h4>
        </div>
        <div className="card-body">
          <div className="add-category">
            <form>
              <div className="form-group">
                <label htmlFor="category">Tên danh mục</label>
                <input

                  type="text"
                  className="form-control"
                  id="exampleInputEmail1"
                  aria-describedby="emailHelp"
                  placeholder="Nhập tên danh mục"
                />
              </div>
            </form>
          </div>
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>ID</th>
                <th>Tên danh mục</th>
                <th>Slug</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>{Comment_HTMLTABLE}</tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CategoryManagement;
