import React, { useState, useRef } from "react";
import "../Question/ContentQuestion.css";
function AddComment() {
  return (
    <div className="comment-container">
      <div className="leave-comment">
        <h6>Leave a comment</h6>
      </div>
      <form onSubmit={submitQuestion} encType="multipart/form-data">
            <div className="form-group">
              <label>
                Tiêu đề{" "}
                <b
                  style={{
                    color: "red",
                    fontSize: "24px",
                  }}
                >
                  *
                </b>
              </label>
              <input
                type="text"
                name="title"
                onChange={handleInput}
                value={questionInput.title}
                className="form-control"
                placeholder="Nhập tiêu đề câu hỏi"
              />
            </div>

            <div className="note">
              <p>
                Vui lòng đặt tiêu đề thích hợp cho câu hỏi để người khác được
                trả lời dễ dàng hơn.
              </p>
            </div>
            {/* Create category */}
            <div className="form-group">
              <label>
                Danh mục câu hỏi{" "}
                <b
                  style={{
                    color: "red",
                    fontSize: "24px",
                  }}
                >
                  *
                </b>
              </label>
              <div className="category-container">
                <select
                  name="category_question_id"
                  onChange={handleInput}
                  value={questionInput.category_question_id}
                  className="form-control"
                >
                  <option>Chọn danh mục</option>
                  {categoryQuestionList.map((item) => {
                    return (
                      <option key={item.id} value={item.id}>
                        {item.name}
                      </option>
                    );
                  })}
                </select>
                <button
                  className="add-category-btn form-control-submit-button"
                  onClick={AddTag1}
                >
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
            <p>Vui lòng chọn danh mục thích hợp</p>
            {/* End create category */}
            <div class="form-group">
              <label for="">Nội dung</label>
              <textarea
                class="form-control"
                name="content"
                onChange={handleInput}
                value={questionInput.content}
                rows="3"
                placeholder="Nội dung/Đoạn code"
              ></textarea>
              
            </div>
            <p>Bạn có thể không nhập nội dung</p>
            {/* Create a tag*/}
            <div className="form-group">
              <label>
                Tag{" "}
                <b
                  style={{
                    color: "red",
                    fontSize: "24px",
                  }}
                >
                  *
                </b>
              </label>
              <div className="tag-container">
                <select
                  name="tag_id"
                  onChange={handleInput}
                  value={questionInput.tag_id}
                  className="form-control"
                >
                  <option>Chọn tag</option>
                  {tagList.map((item) => {
                    return (
                      <option key={item.id} value={item.id}>
                        {item.name}
                      </option>
                    );
                  })}
                </select>
                <button
                  className="add-tag-btn form-control-submit-button"
                  onClick={AddTag}
                >
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
            <p>Vui lòng chọn tag phù hợp với câu hỏi</p>
            {/* End create a tag*/}
            {/* Create a ask question button*/}
            <div className="form-group">
              <button type="submit" className="form-control-submit-button">
                Đặt câu hỏi
              </button>
            </div>
          </form>
    
    </div>
  );
}

export default AddComment;
