import React, { useEffect } from "react";
import "./ItemQuestion.css";
import axios from "axios";
function ItemQuestion() {
  const [question, setQuestion] = React.useState("");
  useEffect(() => {
    axios.get(`api/get-all-question`).then((res) => {
      if (res.data.status === 200) {
        setQuestion(res.data.data);
      }
    });
  }, []);
  question.map((item) => {
    return (
      <div>
        <hr></hr>
        <div className="container-item-question">
          <div className="vote-box">
            <h5>
              <b>{item.count_vote}</b>
            </h5>
            <p>votes</p>
          </div>
          <div className="answer-quality-box">
            <h5>
              <b>{item.count_vote}</b>
            </h5>
            <p>answers</p>
          </div>
          <div className="view-box">
            <h5>
              <b>20</b>
            </h5>
            <p>views</p>
          </div>
          <div className="main-box">
            <div className="question-box">
              <h3>Lập trình hướng đối tượng là gì?</h3>
            </div>
            <div className="tag-box">
              <p>kien-thuc-chung</p>
              <p>oop</p>
              <p>programming</p>
            </div>
            <div className="info-box">
              <i className="fas fa-briefcase">Lập trình viên</i>
              <i className="fas fa-user">quangthin2000</i>
              <i className="fas fa-calendar-alt">20-03-2021</i>
            </div>
          </div>
        </div>
      </div>
    );
  });
}

export default ItemQuestion;
