import axios from "axios";
import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import swal from "sweetalert";

function CuttingArray(choices) {
    if (!choices) return '';

    const choiceNew = choices[0].split(',');

    return choiceNew;
}



function Result(props) {

    const history = useHistory();
    const [answer, setAnswer] = useState([]);
    const [quiz, setQuiz] = useState([]);
    const [loading, setLoading] = useState(true);
    const [quizId, setQuizId] = useState([]);
    const [correct, setCorrect] = useState([]);
    const [choice, setChoice] = useState([]);

    if (!localStorage.getItem('auth_token')) {
        history.push('/');
        swal("warning", "Login to view result", "error");
    }

    // console.log("du lieu cua nguoi dung lam : ", data[0].split(','));

    useEffect(() => {

        let isMountered = true;

        axios.get(`/api/get-answer`).then(res => {
            if (isMountered) {
                if (res.data.status === 200) {
                    setAnswer(res.data.answer);
                    setLoading(false);
                } else if (res.data.status === 401) {
                    history('/');
                    swal("warning", res.data.message, "error");
                }
            }
        });

        axios.get(`/api/view-quiz`).then(res => {
            if (res.data.status === 200) {
                setQuiz(res.data.quizzes);
                setLoading(false);
            }
        });

        return () => {
            isMountered = false;
        }
    }, [history]);

    useEffect(() => {
        setQuizId(quiz.map(item => item.item_id));

        setCorrect(quiz.map(item => item.ans_correct));

        setChoice(answer.map(item => item.quiz_choice));

        // return () => {
        //     clearInterval(setquiz);
        //     clearInterval(setcorrect);
        //     clearInterval(setchoice);
        // }

    }, [quiz, answer]);

    if (loading) {
        return (
            <h4>Loading Result...</h4>
        )
    } else {
        // var showResult = "";
        // showResult = 0;
    }


    // console.log('du lieu nguoi dung lam :', Cutting(choice));
    // console.log('dap an dung :', correct);
    // console.log('id bo cau hoi :', quizId);

    return (
        <div>
            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Kết quả</h1>
                        </div>
                    </div>
                </div>
            </header>

            <div className="container">
                <div className="row">
                    <div className="card">
                        <div className="card-content">
                            <div className="content">
                                <h3>Result</h3>
                                <p>đáp án đúng / 10</p>
                                <p><strong>80%</strong></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Result;
