import React, { useEffect, useState } from "react";
import axios from "axios";
import "./SearchQuestion.css";
function SearchQuestion() {
  const [searchInput, setSearchInput] = useState({
    name: "",
  });
  const handleInput = (e) => {
    e.persist();
    setSearchInput({ ...setSearchInput, [e.target.name]: e.target.value });
  };
  const [searchQuestion, setSearchQuestion] = useState("");
  useEffect(() => {
    axios.get(`/api/question/search/${searchInput}`).then((res) => {
      setSearchQuestion(res.data.data);
      console.log(res.data.data);
    });
  }, []);

  return (
    <div className="container search-question-container">
      {/* <form>
        <input
          className="form-control search-input"
          type="text"
          placeholder="Bạn muốn tìm gì?"
          onChange={handleInput}
          value={searchInput.name}
        />
        <button className="btn search-btn" id="btnNavbarSearch" type="submit">
          <i className="fas fa-search icon-btn" />
        </button>
      </form> */}
      <form
        action="/search"
        className="form-inline my-2 my-lg-0"
      >
        <div className="input-group">
          <input
            name="query"
            className="form-control search-input"
            type="text"
            placeholder="Bạn muốn hỏi gì"
            aria-label="Search for..."
            aria-describedby="btnNavbarSearch"
          />
          <button className="btn search-btn" id="btnNavbarSearch" type="submit">
            <i className="fas fa-search icon-btn" />
          </button>
        </div>
      </form>
    </div>
  );
}

export default SearchQuestion;
