import axios from "axios";
import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import swal from "sweetalert";

function Result() {

    const history = useHistory();
    const [answer, setAnswer] = useState([]);
    const [loading, setLoading] = useState(true);

    if (!localStorage.getItem('auth_token')) {
        history.push('/');
        swal("warning", "Login to view result", "error");
    }

    useEffect(() => {

        let isMountered = true;
        const answer_id = props.match.params.id;

        axios.get(`/api/get-answer/${answer_id}`).then(res => {
            if (isMountered) {
                if (res.data.status === 200) {
                    setAnswer(res.data.answer);
                    setLoading(false);
                } else if (res.data.status === 401) {
                    history('/');
                    swal("warning", res.data.message, "error");
                }
            }
        })

        return () => {
            isMountered = false;
        }
    }, [props.match.params.id]);

    console.log(answer.quiz_choice);

    if (loading) {
        return (
            <h4>Loading Result...</h4>
        )
    }

    return (
        <div>
            <header className="ex-header">
                <div className="container">
                    <div className="row">
                        <div className="col-xl-10 offset-xl-1">
                            <h1 className="text-center">Kết quả</h1>
                        </div>
                    </div>
                </div>
            </header>

            <div>
                ket qua
            </div>
        </div>
    )
}

export default Result;
