import React, {useState, useEffect} from "react";
import Footer from "../../../layouts/frontend/Footer";
import "./styles.css";
import { useLocation } from "react-router-dom";
import axios from "axios";
const UserProfile = () => {
    const username =
    useLocation().pathname.split("/")[
      useLocation().pathname.split("/").length - 1
    ];
    console.log(username);
    const [profileList, setProfileList] = useState([]);
    useEffect(() => {
        axios.get(`api/user/${username}`).then((res) => {
          if (res.data.status === 200) {
            setProfileList(res.data.data);
            console.log(res.data.data)
          }
        });
      }, []);

      
  return (
    <>
      <header className="ex-header">
        <div className="container">
          <div className="row">
            <div className="col-xl-10 offset-xl-1">
              <h1 className="text-center">Xem thông tin cá nhân</h1>
            </div>
          </div>
        </div>
      </header>
      
      <div className="profile-container">
          
        <div className="left-container">
          <div className="avatar">
            <img
              src="https://gtjai.com.vn/wp-content/uploads/2021/07/avt.png"
              alt="avatar"
            />
            <div className="full-name">
                <h4>Trần Quang Thìn</h4>
            </div>
            <i className="fas fa-edit" style={{fontSize:"30px", marginTop:"20px"}} />

          </div>
        </div>
        <div className="right-container">
            <h4>Thông tin cá nhân</h4>
            <hr></hr>
            <div className="fullname">
                <h5>Họ và tên</h5>
                <h6>Trần Quang Thìn</h6>
            </div>
            <div className="username">
                <h5>Tên đăng nhập</h5>
                <h6>tranqthin</h6>
            </div>
            <div className="email">
                <h5>Email</h5>
                <h6>quangthin</h6>
            </div>
            <div className="phone">
                <h5>Số điện thoại</h5>
                <h6>0987654321</h6>
            </div>
            <div className="created_at">
                <h5>Ngày tạo tài khoản</h5>
                <h6>20/07/2020</h6>
            </div>
        </div>
      </div>
      <Footer/>
    </>
  );
};

export default UserProfile;
