import React, { useEffect, useState } from "react";
import "./ItemQuestion.css";
import axios from "axios";
import { Link } from "react-router-dom";
import moment from "moment";
function ItemQuestion() {
  const [question, setQuestion] = useState([]);
  useEffect(() => {
    axios.get(`api/get-all-question`).then((res) => {
      if (res.data.status === 400) {
        setQuestion(res.data.data);
        console.log(res.data.data);
      }
    });
  }, []);
  var questionList = "";
  questionList = question.map((item) => {
    return (
      <>
        <hr style={{ width: "1000px", marginLeft: "50px" }}></hr>

        <div className="container-item-question">
          <div className="vote-box">
            <h5>
              <b>{item.votes_couter}</b>
            </h5>
            <p>votes</p>
          </div>
          <div className="answer-quality-box">
            <h5>
              <b>{item.comments_couter}</b>
            </h5>
            <p>answers</p>
          </div>
          <div className="view-box">
            <h5>
              <b>{item.views_couter}</b>
            </h5>
            <p>views</p>
          </div>
          <div className="main-box">
            <div className="question-box">
              <h3>
                {/* <Link to={`/question/${item.slug}`}>{item.title}</Link> */}
              </h3>
            </div>
            <div className="tag-box">
              <p>{item.tags.name}</p>
            </div>
            <div className="info-box">
              <i className="fas fa-briefcase">{item.category_question.name}</i>
              <i className="fas fa-user">{item.user.username}</i>
              <i className="fas fa-calendar-alt">
                asked {moment(item.created_at).fromNow(true)} ago
              </i>
            </div>
          </div>
        </div>
      </>
    );
  });

  return <div>{questionList}</div>;
}

export default ItemQuestion;
